mapper.select:
SELECT DISTINCT oo.ORDER_OPTION_ID FROM order_option_tbl oo WHERE oo.ORDER_ID = #{orderId} AND oo.MENU_ITEM_ID = #{menuItemId} AND ( SELECT COUNT(1) FROM order_option_tbl WHERE ORDER_OPTION_ID = oo.ORDER_OPTION_ID ) = #{optionCount} AND NOT EXISTS ( SELECT 1 FROM (
SELECT #{option.option_group_id} AS OPTION_GROUP_ID, #{option.option_id} AS OPTION_ID FROM DUAL
) opt WHERE NOT EXISTS ( SELECT 1 FROM order_option_tbl WHERE ORDER_OPTION_ID = oo.ORDER_OPTION_ID AND OPTION_GROUP_ID = opt.OPTION_GROUP_ID AND OPTION_ID = opt.OPTION_ID ) ) AND NOT EXISTS ( SELECT 1 FROM order_option_tbl WHERE ORDER_OPTION_ID = oo.ORDER_OPTION_ID AND NOT EXISTS ( SELECT 1 FROM (
SELECT #{option.option_group_id} AS OPTION_GROUP_ID, #{option.option_id} AS OPTION_ID FROM DUAL
) opt WHERE opt.OPTION_GROUP_ID = OPTION_GROUP_ID AND opt.OPTION_ID = OPTION_ID ) ) AND ROWNUM = 1

mapper.update:
UPDATE order_quantity_tbl
SET QUANTITY = QUANTITY +
#{additionalQuantity}
WHERE ORDER_OPTION_ID = #{orderOptionId}
AND
ORDER_ID = #{orderId}

mapper.select:
SELECT MAX(ORDER_OPTION_ID) FROM order_option_tbl WHERE
ORDER_ID =
#{orderId}

select user_phone, point, user_address from b_user_info
where
user_id=#{user_id}

mapper.update:
UPDATE b_order_detail_tbl
SET store_id = #{store_id},
store_address = #{store_address}
WHERE order_id = #{order_id}
